def get_s3():
    try:
        return boto3.client("s3", aws_access_key_id=deviare_settings.deviare_config["AWS_S3_ACCESS_KEY"],
                            aws_secret_access_key=deviare_settings.deviare_config["AWS_S3_SECRET_KEY"])
    except Exception as e:
        return False


def put_s3(Body='', key=''):
    s3 = get_s3()
    if s3 is False:
        return s3
    kwargs = dict(
        Body=Body,
        Bucket=deviare_settings.deviare_config["AWS_S3_BUCKET_NAME"],
        Key=key,
        ACL="public-read"
    )
    # print(kwargs, deviare_settings.deviare_config)
    try:
        s3.put_object(**kwargs)
        return "https://{Bucket}.s3.amazonaws.com/{Key}".format(**kwargs)
    except Exception as e:
        traceback.print_exc()
        traceback.print_stack()
        print('__________________________________________________')


def get_remote_data(url):
    r = requests.get(url)
    if r.ok:
        return r.content
    if r.status_code == 404:
        return False


class ResponseDocument(object):

    def __init__(self, user_response):
        self.user_response = user_response
        self._s3_path = None
        self._s3_zip = None
        self.fp = BytesIO()
        if self.user_response.document:
            doc = self.user_response.document
            if doc != self.s3_path:
                data = get_remote_data(doc)
                u, fn = doc.rsplit('/', 1)
                self.s3_zip.writestr(fn, data)
        self.user_response.document = self.s3_path

    @property
    def s3_zip(self):
        if not self._s3_zip:
            data = get_remote_data(self.s3_path)
            if data is not False:
                self.fp = BytesIO(data)
            ZipFile(self.fp, 'w', ZIP_DEFLATED)
        return self._s3_zip

    @property
    def key(self):
        ser = TMForumUserResponseDocSerializer(instance=self.user_response, fields=['owner', 'criterion_id']).data
        return f'tmp/customer/{ser["owner"]}/{ser["criterion_id"]}.zip'

    @property
    def data(self):
        self.s3_zip
        return b64encode(self.fp.getvalue()).decode('utf-8')

    @property
    def s3_path(self):
        if not self._s3_path:
            kwargs = {
                'Bucket': deviare_settings.deviare_config["AWS_S3_BUCKET_NAME"],
                'key': self.key
            }
            self._s3_path = "https://{Bucket}.s3.amazonaws.com/{key}".format(**kwargs)
        return self._s3_path

    def add(self, path='', data=b''):
        data_cid, cid_ex = return_image_extenstion(data)
        self.s3_zip.writestr(path, data_cid)

    def __enter__(self):
        return self.s3_zip

    def __exit__(self, exc_type, exc_value, exc_traceback):
        fn = put_s3(self.data, self.key)
        if self.fp is not None:
            self.fp.close()
        if self.s3_zip is not None:
            self.s3_zip.close()
        if fn is False:
            raise Exception('s3 update failed')
        self.user_response.save()

    @classmethod
    def process_documents(cls, user_response, document=None):
        with cls(user_response) as f:
            if document:
                if type(document) in (list, tuple):
                    for doc in document:
                        f.add(**doc)
                else:
                    f.add(**document)


class IsOwnerFilterBackend(rest_filter.BaseFilterBackend):
    """
    Filter that only allows users to see their own objects.
    """
    def filter_queryset(self, request, queryset, view):
        if request.userAuth:
            if request.userAuth.role in ["projectadmin", "user", "customeradmin"]:
                return queryset.filter(owner=request.userAuth)
        return queryset


class IsOwnerCompanyFilterBackend(rest_filter.BaseFilterBackend):
    """
    Filter that only allows users to see their own objects.
    """
    def extract_filter_kwargs(self, data, role):
        kw = {}
        map_kw = {
            'company': 'owner__companyID',
            'project': 'owner__companyID__projects__uuid',
            'user': 'owner'
        }
        for k, v in data.items():
            if k in map_kw:
                k = f'{map_kw.get(k, k)}__in' if type(v) in (tuple, list) else map_kw.get(k, k)
                kw[k] = v
        return kw

    def filter_queryset(self, request, queryset, view):
        if request.userAuth:
            query_data = request.data
            user = request.userAuth
            role = user.role
            query_data.update(request.query_params)
            kw = self.extract_filter_kwargs(query_data, role)

            if role in ["projectadmin", "user", "customeradmin"]:
                if role == 'user':
                    return queryset.filter(owner=request.userAuth)
                elif role == "projectadmin":
                    return queryset.filter(
                        owner__registered_assessments__assessment_license_id__project_id__in=user.allocated_project.all(),
                        **kw)
                else:
                    return queryset.filter(
                        # assigned__assigned__company=request.userAuth.companyID,
                        **kw)
        return queryset.filter(**kw)


class BaseModelViewSet(ModelViewSet):

    response_class = ResponseWrapper
    pagination_class = RestPagination
    list_serializer_class = None
    # permission_classes = [IsAuthenticated]

    @data_response
    def create(self, request, *args, **kwargs):
        if type(request.data) in (list, tuple):
            serializer = serializers.ListSerializer(
                data=request.data,
                child=self.serializer_class(
                    context=self.get_serializer_context()
                )
            )
        else:
            serializer = self.get_serializer(data=request.data)
        if serializer.is_valid(raise_exception=True):
            self.perform_create(serializer)
            headers = self.get_success_headers(serializer.data)
            return dict(
                data=serializer.data,
                status=S.HTTP_201_CREATED,
                headers=headers,
                msg='Successfully Created Entry'
            )
        else:
            return dict(success=False, msg="Incorrect Data")

    def perform_create(self, serializer):
        serializer.save()

    @data_response
    def update(self, request, *args, **kwargs):
        data = request.data
        try:
            instance = self.get_object()
            partial = kwargs.pop('partial', True)
            serializer = self.get_serializer(instance, data=data, partial=partial)
            serializer.is_valid(raise_exception=True)
            self.perform_update(serializer)

            if getattr(instance, '_prefetched_objects_cache', None):
                # If 'prefetch_related' has been applied to a queryset, we need to
                # forcibly invalidate the prefetch cache on the instance.
                instance._prefetched_objects_cache = {}

            return dict(data=serializer.data, status=S.HTTP_202_ACCEPTED, success=True, msg='Data Saved')
        except Exception as e:
            traceback.print_stack()
            traceback.print_exc()
            return dict(success=False, msg=str(e), status=406)

    def flatten_data(self, result, request, *args, **kwargs):
        # flatten = request.query_params.get('flatten', kwargs.get('flatten', False))
        # if flatten is not False:
        #     result = list(map(as_lookup, result)) if type(result) in (list, tuple, ReturnList) else as_lookup(result)
        return result

    def get_serializer(self, *args, **kwargs):
        """
        Return the serializer instance that should be used for validating and
        deserializing input, and for serializing output.
        """
        serializer_class = self.get_serializer_class()
        kwargs['context'] = self.get_serializer_context()
        if self.request is not None:
            if self.request.method == 'GET':
                if self.list_serializer_class and kwargs.get('many', False):
                    return self.list_serializer_class(*args, **kwargs)
        return serializer_class(*args, **kwargs)

    def get_paginated(self, data):
        """
        Return a paginated style `Response` object for the given output data.
        """
        assert isinstance(self.paginator, RestPagination)
        return self.paginator.get_paginated(data)
    # def filter_queryset(self, queryset):

    def list_data(self, request, *args, **kwargs):
        queryset = self.filter_queryset(self.get_queryset())

        page = self.paginate_queryset(queryset)
        if page is not None:
            serializer = self.get_serializer(page, many=True)
            return self.get_paginated(
                self.flatten_data(serializer.data, request, *args, **kwargs)
            )

        serializer = self.get_serializer(queryset, many=True)
        return self.flatten_data(serializer.data, request, *args, **kwargs)

    @data_response
    def list(self, request, *args, **kwargs):

        if not self.request:
            self.request = request
        try:
            result = self.list_data(request, *args, **kwargs)
        except Exception as e:
            raise dict(success=False,  msg=str(e), status=404)
        return dict(data=result, success=True, msg='Got records', status=S.HTTP_200_OK)

    def retrieve_data(self, request, *args, **kwargs):
        instance = self.get_object()
        serializer = self.get_serializer(instance)
        return self.flatten_data(serializer.data, request, *args, **kwargs)

    @data_response
    def retrieve(self, request, *args, **kwargs):
        result = self.retrieve_data(request, *args, **kwargs)
        return dict(data=result, success=True, msg='Data Found', status=S.HTTP_200_OK)

    @property
    def auth_role(self):
        if self.auth_user:
            return self.auth_user.role
        return 'Anonymous'

    @property
    def auth_user(self):
        if self.request is not None:
            user = self.request.userAuth
            if user is not None:
                return user
        return None


def get_download_response(data, filename):
    from django.shortcuts import HttpResponse
    import mimetypes
    mime, ext = mimetypes.guess_type(filename)
    response = HttpResponse(content=data, charset='utf-8', content_type=mime)
    response['Content-Disposition'] = f'attachment; filename={filename}'
    response['Content-Length'] = len(data)
    return response


