class TMForumUserViewset(BaseModelViewSet):
    queryset = TMForumUserAssessment.objects.all()
    serializer_class = TMForumUserSerializer

    @data_response
    def create(self, request, *args, **kwargs):
        from pprint import pprint
        if not request.userAuth:
            return dict(success=False, msg='You need to log in')
        pprint(kwargs, indent=2)
        owner = request.data.get('owner', None)
        if owner is None:
            return dict(success=False, msg='You need to select a user to complete this assessment')
        company = request.data.get('company', None)
        if company is None:
            return dict(success=False, msg='You need to select a project to complete this assessment')

        assign, created = TMForumAssignedAssessment.objects.get_or_create(owner_id=owner, company_id=company)
        if created:
            assign.save()
        subs = [dict(
            owner_id=owner,
            sub_dimension_id=sub
        ) for sub, selected in request.data.get('sub_dimensions', {}).items() if selected is not False]
        # o = TMForumUserAssessment.objects.bulk_create(subs)
        # print(o)
        for skw in subs:
            t, c = TMForumUserAssessment.objects.get_or_create(**skw)
            if c:
                t.save()
                assign.assessment.add(t)
        qs = TMForumUserAssessment.objects.filter(owner_id=owner)
        serializer = self.get_serializer(qs, many=True)

        return dict(success=True, msg='Successfully assigned', data=serializer.data)


class TMForumUserResponseViewset(BaseModelViewSet):
    queryset = TMForumUserResponse.objects.all()
    serializer_class = TMForumUserResponseSerializer
    list_serializer_class = TMForumUserResponseListSerializer
    filter_backends = [IsOwnerFilterBackend, DjangoFilterBackend]

    @data_response
    def create(self, request, *args, **kwargs):
        if not request.userAuth:
            return dict(success=False, msg='You need to log in')
        crit_id = request.data.get('criterion')
        resp = request.data.pop('response')
        request.data.update(resp)
        uuid = resp.get('uuid', False)
        check = dict(owner=request.userAuth)
        request.data.update(check)
        check['criterion_id'] = crit_id
        if uuid is not False:
            check['uuid'] = uuid
        try:
            user_response, created = TMForumUserResponse.objects.using('default').get_or_create(**check)
            for k, v in resp.items():
                if k not in check.keys():
                    if k in ['status_quo', 'aspiration']:
                        k = f'{k}_id'
                    setattr(user_response, k, v)
            if created:
                print("#############################   CREATED   ###################################")
                user_response.save()
                user_response.refresh_from_db()
                print(user_response, user_response.uuid)
        except Exception as e:
            traceback.print_exc()
            traceback.print_stack()
            return dict(success=False, msg=f"Can't Create Response: {str(e)}", status=S.HTTP_400_BAD_REQUEST)

        if (resp.get("document") and (
                type(resp.get("document")) in (list, tuple) or
                type(resp.get("document")) == dict and resp.get("document").get("path"))
                or uuid is not False):
            try:
                doc = resp.pop("document")

                if doc and (type(doc) in (list, tuple) or type(doc) == dict and doc.get("path")):
                    data_cid, cid_ex = return_image_extenstion(doc.get("data"))
                    rs = request.user.username
                    key = "tmp/customer/{}/{}".format(rs, doc.get("path")).replace(" ", "_")

                    document = put_s3(Body=data_cid, key=key)
                    if document is False:
                        return dict(success=False, msg="Can't connect to S3", status=S.HTTP_400_BAD_REQUEST)
                    else:
                        request.data.update({"document": document})
                        user_response.document = document
                user_response.save()
                ser = self.get_serializer(user_response)
                return dict(success=True, data=ser.data, msg="Saved Response", status=S.HTTP_200_OK)

            except Exception as e:
                traceback.print_exc()
                traceback.print_stack()
                return dict(success=False, msg=f"Can't Upload Evidence: {str(e)}", status=S.HTTP_400_BAD_REQUEST)

        # return super().create(request, *args, **kwargs)
        ser = self.get_serializer(user_response)
        return dict(success=True, data=ser.data, msg="Saved Response", status=S.HTTP_200_OK)


